import React, { useState } from 'react';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';

// Configure axios defaults
axios.defaults.baseURL = 'http://localhost:5001';
axios.defaults.headers.post['Content-Type'] = 'application/json';
axios.defaults.withCredentials = true;

function App() {
  const [textPrompt, setTextPrompt] = useState('');
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [loadingStep, setLoadingStep] = useState('');
  const [error, setError] = useState(null);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    accept: {
      'image/*': ['.jpeg', '.jpg', '.png']
    },
    maxFiles: 1,
    onDrop: async (acceptedFiles) => {
      const file = acceptedFiles[0];
      const reader = new FileReader();

      reader.onload = async () => {
        const base64Image = reader.result.split(',')[1];
        await getRecommendations(null, base64Image);
      };

      reader.readAsDataURL(file);
    }
  });

  const getRecommendations = async (text, image) => {
    setLoading(true);
    setError(null);
    setLoadingStep('Analyzing your input...');

    try {
      console.log('Sending request to backend...');
      const response = await axios.post('/api/recommend', {
        text_prompt: text,
        image: image
      }, {
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        }
      });

      console.log('Received response:', response.data);
      setRecommendations(response.data.recommendations);
    } catch (err) {
      console.error('Error details:', err);
      if (err.code === 'ERR_NETWORK') {
        setError('Cannot connect to the server. Please make sure the backend is running on http://localhost:5001');
      } else if (err.response) {
        setError(`Server error: ${err.response.data.error || err.message}`);
      } else {
        setError(`Error: ${err.message}`);
      }
    } finally {
      setLoading(false);
      setLoadingStep('');
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (textPrompt.trim()) {
      getRecommendations(textPrompt);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 py-6 flex flex-col justify-center sm:py-12">
      <div className="relative py-3 sm:max-w-xl sm:mx-auto">
        <div className="relative px-4 py-10 bg-white shadow-lg sm:rounded-3xl sm:p-20">
          <div className="max-w-md mx-auto">
            <div className="divide-y divide-gray-200">
              <div className="py-8 text-base leading-6 space-y-4 text-gray-700 sm:text-lg sm:leading-7">
                <h1 className="text-3xl font-bold text-center mb-8 text-indigo-600">
                  Music Playlist Recommender
                </h1>

                {/* Text Input */}
                <form onSubmit={handleSubmit} className="mb-8">
                  <div className="relative">
                    <input
                      type="text"
                      value={textPrompt}
                      onChange={(e) => setTextPrompt(e.target.value)}
                      placeholder="Describe your mood or music preferences..."
                      className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
                      disabled={loading}
                    />
                    <button
                      type="submit"
                      className="absolute right-2 top-2 bg-indigo-600 text-white px-4 py-1 rounded-md hover:bg-indigo-700 transition-colors disabled:opacity-50"
                      disabled={loading}
                    >
                      Get Recommendations
                    </button>
                  </div>
                </form>

                {/* Image Upload */}
                <div className="mb-8">
                  <div
                    {...getRootProps()}
                    className={`border-2 border-dashed rounded-lg p-8 text-center cursor-pointer transition-colors
                      ${isDragActive ? 'border-indigo-500 bg-indigo-50' : 'border-gray-300 hover:border-indigo-500'}
                      ${loading ? 'opacity-50 cursor-not-allowed' : ''}`}
                  >
                    <input {...getInputProps()} disabled={loading} />
                    {isDragActive ? (
                      <p>Drop the image here...</p>
                    ) : (
                      <p>Drag and drop an image here, or click to select one</p>
                    )}
                  </div>
                </div>

                {/* Loading State */}
                {loading && (
                  <div className="text-center space-y-4">
                    <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600 mx-auto"></div>
                    <p className="text-indigo-600">{loadingStep}</p>
                    <p className="text-sm text-gray-500">This may take a few moments...</p>
                  </div>
                )}

                {/* Error Message */}
                {error && (
                  <div className="text-red-500 text-center mb-4">
                    {error}
                  </div>
                )}

                {/* Recommendations */}
                {recommendations.length > 0 && (
                  <div className="mt-8">
                    <h2 className="text-xl font-semibold mb-4">Recommended Tracks</h2>
                    <div className="space-y-4">
                      {recommendations.map((track, index) => (
                        <div
                          key={index}
                          className="flex items-center justify-between p-4 bg-gray-50 rounded-lg"
                        >
                          <div>
                            <h3 className="font-medium">{track.name}</h3>
                            <p className="text-sm text-gray-600">
                              {track.artist} • {track.album}
                            </p>
                          </div>
                          <div className="flex space-x-2">
                            {track.preview_url && (
                              <audio
                                controls
                                className="h-8"
                                src={track.preview_url}
                              />
                            )}
                            <a
                              href={track.external_url}
                              target="_blank"
                              rel="noopener noreferrer"
                              className="text-indigo-600 hover:text-indigo-800"
                            >
                              Open in Spotify
                            </a>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
